//switch statements

class Main17 {
    public static void main(String[] args) {
        char day = 'f';
        switch(day)

        {
            case 'm':
                System.out.println ("Monday ");
                break;
            case 'f':
                System.out.println ("Friday ");
                break;
                
            case 'w':
                System.out.println ("Wednesday ");
                break;
        }
}
}
